        {{-- Content Wrapper --}}
        @empty($iFrameEnabled)
            <div class="content-wrapper">
                {{-- Content Header --}}
                @yield('content_header')

                {{-- Main Content --}}
                <section class="content">
                    @yield('content')
                </section>
            </div>
